	public function captcha(){
		//配置
		$cfg = array(
				'fontSize'  =>  12,              // 验证码字体大小(px)
		        'useCurve'  =>  false,            // 是否画混淆曲线
		        'useNoise'  =>  false,            // 是否添加杂点	
		        'length'    =>  4,               // 验证码位数
		        'imageH'	=>	38,
		        'imageW'	=>	90,	
		        'fontttf'   =>  '4.ttf',              // 验证码字体，不设置随机获取
			);
		//实例化验证码类
		$verify = new \Think\Verify($cfg);
		//输出验证码
		$verify -> entry();
	}
	
